import React, { useState } from "react";
import "../Styles/Side.css";
import { motion } from "framer-motion";
import Icon from "../Details/Icon.tsx";
import Settings from "../Details/Settings.tsx";

const Side: React.FC = () => {
  const [selected, setSelected] = useState<string>("");
  const [showSettings, setShowSettings] = useState<boolean>(false);

  const icons = ["home", "wallet", "statistics"];
  return (
    <div className="side-container">
      <motion.div className="menu-container">
        {icons.map((iconName) => (
          <Icon
            key={iconName}
            name={iconName}
            selected={selected}
            onClick={() => setSelected(iconName)}
          />
        ))}
      </motion.div>
      <Settings showSettings={showSettings} setShowSettings={setShowSettings} />
      {showSettings && (
        <motion.div
          style={{
            position: "absolute",
            top: "20%",
            left: "8%",
            borderRadius: "25px",
            opacity: 0.7,
            width: 500,
            height: 400,
            padding: "50px",
            backgroundColor: "rgb(0, 71, 93)",
          }}
          initial={{}}
        >
          <motion.div>its Modal </motion.div>
        </motion.div>
      )}
    </div>
  );
};

export default Side;
