import React, { useState } from "react";
import "../Styles/Side.css";
import { motion } from "framer-motion";
import Icon from "./Icon.tsx";

const Side: React.FC = () => {
  const [selected, setSelected] = useState<string>("");
  const [notificationOn, setNotificationOn] = useState<boolean>(true);

  const icons = ["home", "wallet", "statistics"];

  return (
    <div className="side-container">
      <motion.div className="menu-container">
        {icons.map((iconName) => (
          <Icon
            key={iconName}
            name={iconName}
            selected={selected}
            onClick={() => setSelected(iconName)}
          />
        ))}
      </motion.div>
      <motion.div className="settings-container">
        <motion.div
          className="notifications-icon"
          onClick={() => setNotificationOn(!notificationOn)}
        >
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: notificationOn ? "100%" : 0 }}
            transition={{ duration: 0.5 }}
            style={{
              height: "3px",
              backgroundColor: "black",
              position: "absolute",
              top: "50%",
              rotateZ: -45,
              left: "50%",
              originX: 0.5,
              originY: 0.5,
              x: "-50%",
            }}
          />
        </motion.div>
      </motion.div>
    </div>
  );
};

export default Side;
