import React, { useState } from "react";
import "../Styles/Side.css";
import { motion, AnimatePresence } from "framer-motion";
import Icon from "../Details/Icon.tsx";
import Settings from "../Details/Settings.tsx";

const Side: React.FC = () => {
  const [selected, setSelected] = useState<string>("");
  const [showSettings, setShowSettings] = useState<boolean>(false);

  const icons = ["home", "wallet", "statistics"];

  // Определяем значения анимации
  const animationProps = {
    x: showSettings ? 10 : 0,
    y: showSettings ? 10 : 0,
    width: showSettings ? 300 : 0,
    height: showSettings ? 400 : 0,
    opacity: showSettings ? 1 : 0,
  };

  return (
    <div className="side-container">
      <motion.div className="menu-container">
        {icons.map((iconName) => (
          <Icon
            key={iconName}
            name={iconName}
            selected={selected}
            onClick={() => {
              setSelected(iconName);
              setShowSettings(false); // Закрываем настройки при выборе другого элемента
            }}
          />
        ))}
      </motion.div>
        {showSettings && (
          <motion.div
            className="showSettings"
            style={{
              position: "absolute",
              top: "20%",
              left: "8%",
              borderRadius: "25px",
              padding: "50px",
              backgroundColor: "rgb(0, 71, 93)",
            }}
            initial={false}
            animate={animationProps}
            transition={{ duration: 0.5 }}
          >
            <motion.div>its Modal</motion.div>
            <button onClick={() => setShowSettings(false)}>Close</button>
          </motion.div>
        )}

    </div>
  );
};

export default Side;
