import React, { useState } from "react";
import "../Styles/Side.css";
import { motion } from "framer-motion";
import Icon from "./Icon.tsx";

const Side: React.FC = () => {
  const [selected, setSelected] = useState<string>("");

  const icons = ["home", "wallet", "statistics"];

  return (
    <div className="side-container">
      <motion.div className="menu-container">
        {icons.map((iconName) => (
          <Icon
            key={iconName}
            name={iconName}
            selected={selected}
            onClick={() => setSelected(iconName)}
          />
        ))}
      </motion.div>
      <motion.div className="settings-container">
        <motion.div
          className="notifications-icon"
          initial={{ backgroundImage: "url('../images/onNotif.png')" }}
          animate={{ backgroundImage: "url('../images/onNotif.png')" }}
        ></motion.div>
      </motion.div>
    </div>
  );
};

export default Side;
