import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "../Styles/Dashboard.css";

interface DashboardProps {
  onSelectedChange: (value: string) => void;
}

const Dashboard: React.FC<DashboardProps> = ({ onSelectedChange }) => {
  const [selected, setSelected] = useState<string>("all");

  useEffect(() => {
    onSelectedChange(selected);
  }, [selected, onSelectedChange]);

  return (
    <div>
      <div style={{ fontSize: 40, fontFamily: "system-ui" }}>My Dashboard</div>
      <motion.div style={{ display: "flex" }}>
        <motion.div
          className="dashboardButtons"
          onClick={() => setSelected("all")}
        >
          All
        </motion.div>
        <motion.div
          className="dashboardButtons"
          onClick={() => setSelected("withdrawal")}
        >
          Withdrawal
        </motion.div>
        <motion.div
          className="dashboardButtons"
          onClick={() => setSelected("savings")}
        >
          Savings
        </motion.div>
        <motion.div
          className="dashboardButtons"
          onClick={() => setSelected("deposit")}
        >
          Deposit
        </motion.div>
      </motion.div>
    </div>
  );
};

export default Dashboard;
