import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";

interface DashboardProps {
  setSelected: (value: string) => void;
}

const Dashboard: React.FC<DashboardProps> = ({ setSelected }) => {
  // Устанавливаем значение по умолчанию "all" в локальном состоянии Dashboard
  const [localSelected, setLocalSelected] = useState<string>("all");

  // Передаем значение "all" в Main при первом рендере
  useEffect(() => {
    setSelected(localSelected);
  }, [localSelected, setSelected]);

  const handleSelect = (value: string) => {
    setLocalSelected(value); // Обновляем локальное состояние
    setSelected(value);      // Обновляем состояние в Main
  };

  return (
    <div>
      <div>My Dashboard</div>
      <motion.div onClick={() => handleSelect("all")}>All</motion.div>
      <motion.div onClick={() => handleSelect("withdrawal")}>
        Withdrawal
      </motion.div>
      <motion.div onClick={() => handleSelect("savings")}>Savings</motion.div>
      <motion.div onClick={() => handleSelect("deposit")}>Deposit</motion.div>
    </div>
  );
};

export default Dashboard;
