import React, { useState } from "react";
import "../Styles/Side.css";
import { motion } from "framer-motion";

const Side: React.FC = () => {
  const [selected, setSelected] = useState<string>("");

  const handleSelect = (iconName: string) => {
    setSelected(iconName);
  };

  return (
    <div className="side-container">
      <motion.div className="menu-container">
        <motion.div
          onClick={() => setSelected("home")}
          className="home-icon"
          style={{ position: "relative", overflow: "hidden" }} // Убедитесь, что родительский элемент имеет позицию relative
        >
          <motion.div
            style={{
              backgroundColor: "white",
              borderRadius: "50%", 
              position: "absolute", 
              top: "50%", 
              left: "50%", 
              transform: "translate(-50%, -50%)", 
              zIndex: -1, 
            }}
            initial={{ width: 0, height: 0 }}
            animate={{
              width: selected === "home" ? 50 : 0,
              height: selected === "home" ? 50 : 0,
            }}
            transition={{ type: "spring", stiffness: 300 }}
          ></motion.div>
        </motion.div>
        <motion.div
          onClick={() => setSelected("wallet")}
          className="wallet-icon"
        ></motion.div>
        <motion.div
          onClick={() => setSelected("statistics")}
          className="statistics-icon"
        ></motion.div>
      </motion.div>
    </div>
  );
};

export default Side;
