import React, { useState } from "react";
import { motion } from "framer-motion";
import "../../Styles/All.css";

const All: React.FC = () => {
  const [onHover, setOnHover] = useState<boolean>(false);
  const tables = [1500, 500, 1000];
  return (
    <div>
      <motion.div>
        <motion.div className="revenue-container">
          <motion.div style={{ display: "flex" }}>
            <p style={{ fontSize: 30, marginTop: 0 }}>Revenue Flow</p>
            <div style={{ marginLeft: "auto" }}>View All &gt;</div>
          </motion.div>
          <motion.div style={{ display: "flex" }}>
            <motion.div>
              <div>2.5k$</div>
              <div>2.0k$</div>
              <div>1.5k$</div>
              <div>1k$</div>
              <div>0.5k$</div>
              <div>0$</div>
            </motion.div>
            <div style={{ display: "flex", alignItems: "flex-end" }}>
              {tables.map((name) => (
                <motion.div
                  className="tower"
                  initial={{
                    background:
                      "repeating-linear-gradient(45deg, rgb(149, 67, 255), rgb(149, 67, 255) 2px, rgba(255, 255, 255, 0) 2px, rgba(255, 255, 255, 0) 10px)",
                  }}
                  onHoverStart={() => {
                    setOnHover(true);
                  }}
                  onHoverEnd={() => {
                    setOnHover(false);
                  }}
                  whileHover={{ background: "rgb(149, 67, 255)" }}
                  transition={{ duration: 5 }}
                  style={{
                    alignItems: "flex-end",
                    marginLeft: 20,
                    width: 90,
                    height: name / 10,
                    borderRadius: 25,
                    display: "flex",
                    justifyContent: "center",
                    transformOrigin: "bottom",
                    animation: "move-lines 2s ease-in-out infinite",
                  }}
                >
                  {onHover}
                  <motion.div
                    style={{
                      position: "absolute",
                      transformOrigin: "bottom",
                      background: "rgb(2, 56, 87)",
                      alignContent: "center",
                      justifyContent: "center",
                      width: 75,
                      opacity: 0.9,
                      paddingLeft: 10,
                      height: 45,
                      borderRadius: 25,
                    }}
                    initial={{ x: 0 }}
                    animate={{ x: -50, y: -name / 10 }}
                  >
                    +${name}
                  </motion.div>
                </motion.div>
              ))}
            </div>
          </motion.div>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default All;
