import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";

interface DashboardProps {
  onSelectedChange: (value: string) => void;
}

const Dashboard: React.FC<DashboardProps> = ({ onSelectedChange }) => {
  // Локальное состояние внутри Dashboard
  const [selected, setSelected] = useState<string>("all");

  // Обновляем состояние в Main при изменении selected в Dashboard
  useEffect(() => {
    onSelectedChange(selected);
  }, [selected, onSelectedChange]);

  return (
    <div>
      <div>My Dashboard</div>
      <motion.div onClick={() => setSelected("all")}>All</motion.div>
      <motion.div onClick={() => setSelected("withdrawal")}>
        Withdrawal
      </motion.div>
      <motion.div onClick={() => setSelected("savings")}>Savings</motion.div>
      <motion.div onClick={() => setSelected("deposit")}>Deposit</motion.div>
    </div>
  );
};

export default Dashboard;
