import React, { useState } from "react";
import { motion } from "framer-motion";
import "../../Styles/All.css";

const All: React.FC = () => {
  const [hoveredIndex, setHoveredIndex] = useState<number | null>(null);
  const tables = [1500, 500, 1000, 123, 0, 2500, 444, 987];

  // Определяем фиксированное значение для высоты меток
  const labelHeight = 40; // Высота каждой метки
  const labelSpacing = 10; // Отступ между метками
  const containerHeight = 300; // Высота контейнера

  // Вычисляем доступную высоту для столбцов
  const availableHeight =
    containerHeight - tables.length * (labelHeight + labelSpacing);

  return (
    <div>
      <motion.div>
        <motion.div className="revenue-container">
          <motion.div style={{ display: "flex" }}>
            <p style={{ fontSize: 30, marginTop: 0 }}>Revenue Flow</p>
            <div style={{ marginLeft: "auto" }}>View All &gt;</div>
          </motion.div>
          <motion.div style={{ display: "flex" }}>
            <motion.div>
              {/* Метки по оси Y с фиксированной высотой и отступами */}
              {tables.map((_, index) => (
                <div key={index} style={{ marginBottom: labelSpacing }}>
                  {(tables.length - index - 1) * 500}$
                </div>
              ))}
            </motion.div>
            <div style={{ display: "flex", alignItems: "flex-end" }}>
              {tables.map((name, index) => (
                <motion.div
                  className="tower"
                  initial={{
                    background:
                      "repeating-linear-gradient(45deg, rgb(149, 67, 255), rgb(149, 67, 255) 2px, rgba(255, 255, 255, 0) 2px, rgba(255, 255, 255, 0) 10px)",
                  }}
                  onHoverStart={() => setHoveredIndex(index)}
                  onHoverEnd={() => setHoveredIndex(null)}
                  whileHover={{ background: "rgb(149, 67, 255)" }}
                  transition={{ duration: 5 }}
                  style={{
                    alignItems: "flex-end",
                    marginLeft: 20,
                    width: 90,
                    height: (name / 2500) * availableHeight, // Высота столбца
                    borderRadius: 25,
                    display: "flex",
                    justifyContent: "center",
                    transformOrigin: "bottom",
                    animation: "move-lines 2s ease-in-out infinite",
                  }}
                >
                  {hoveredIndex === index && (
                    <div style={{ position: "relative" }}>
                      <motion.div
                        style={{
                          width: 20,
                          height: 20,
                          boxShadow:
                            "0 0 10px rgba(255, 255, 255, 1), 0 0 20px rgba(255, 255, 255, 0.5)",
                          border: "10px solid rgba(231, 238, 240, 1)",
                          borderRadius: "100%",
                          zIndex: 4,
                        }}
                        initial={{ x: 0, y: 0 }}
                        animate={{ y: -(name / 2500) * availableHeight + 20 }} // Анимация круга
                      ></motion.div>
                      <motion.div
                        style={{
                          position: "absolute",
                          transformOrigin: "bottom",
                          background: "rgb(2, 56, 87)",
                          alignContent: "center",
                          justifyContent: "center",
                          width: 75,
                          opacity: 0.9,
                          paddingLeft: 10,
                          height: 45,
                          borderRadius: 25,
                        }}
                        initial={{ x: 0, y: 0 }}
                        animate={{
                          x: -70,
                          y: -(name / 2500) * availableHeight - 45,
                        }}
                      >
                        +${name}
                      </motion.div>
                    </div>
                  )}
                </motion.div>
              ))}
            </div>
          </motion.div>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default All;
