import React, { useState } from "react";
import "../Styles/Side.css";
import { motion } from "framer-motion";
import Icon from "./Icon.tsx";
import onNotif from "../images/onNotif.png";
import offNotif from "../images/offNotif.png";

const Side: React.FC = () => {
  const [selected, setSelected] = useState<string>("");
  const [notificationOn, setNotificationOn] = useState<boolean>(true);

  const icons = ["home", "wallet", "statistics"];

  return (
    <div className="side-container">
      <motion.div className="menu-container">
        {icons.map((iconName) => (
          <Icon
            key={iconName}
            name={iconName}
            selected={selected}
            onClick={() => setSelected(iconName)}
          />
        ))}
      </motion.div>
      <motion.div className="settings-container">
        <motion.div
          className="notifications-icon"
          onClick={() => setNotificationOn(!notificationOn)}
          initial={{ backgroundImage: `url(${onNotif})` }}
          animate={{
            backgroundImage: `url(${notificationOn ? onNotif : offNotif})`,
          }}
          transition={{duration}}
        ></motion.div>
      </motion.div>
    </div>
  );
};

export default Side;
